NAMES and ADDRESSES!file addr+sur,,4,26+name,,4,52+addr1,,6,34+addr2,,7,34+addr3,,8,34+addr4,,9,34+addr5,,10,34+htel,,11,34+otel,,12,34*i=insertI01     clear	message "Use BACKSPACE to finish inserting"I02     input sur-name bs=I05	if sur = "" and name = "" then goto I05	read addr nsr=I03	display addr1-otel	error "Already recorded"	endI03     input addr1-otel bs=I02I04     insert addr	goto I01I05     clear	end*f=find	clear	input sur-name bs=F01	find addr	display sur-otelF01     end*n=next	next addr	display sur-otel	end*p=prev	prev addr	display sur-otel	end*m=match	match addr nsr=M01	display sur-otel	endM01     error "No further matching records"	end*a=amend	check addr	goto A02A01     input sur-name bs=A04A02     input addr1-otel bs=A01 eoi=A03A03     write addr	clearA04     end*d=delete	check addr	prompt "Delete this record" no=D01	delete addr	clearD01     end*e=exit	exit