STANDING ORDERS!file so stand!temp date,,d4!temp mode,,a1+date,Today's date,2,70+so_ref,,4,33+so_benf,,6,33+so_amt,,8,38,+so_fpd,,10,39+so_npd,,11,39+so_lpd,,12,39+so_freq,,14,42,u+so_days,,14,63	display date	end*i=insert	clear: display date	mode = "I": gosub INPUT	insert so	clear: display date	end*f=find	clear: display date	input so_ref bs=END	find so	gosub DISPLAY	end*n=next	clear: display date	next so nsr=N1	gosub DISPLAY	endN1      error "End of file"	end*a=amend	check so	mode = "A": gosub INPUT	write so	clear: display date	end*d=delete	check so	prompt "Are you sure" no=D9	delete so	clear: display dateD9      end*e=exit	exitDISPLAY\	display so_ref - so_freq	if so_freq = "D" then display so_days	returnINPUT\	if mode = "A" then goto IN2IN1     input so_ref bs=IN9	testkey so nsr=IN2	error "Already recorded": goto IN1IN2     input so_benf - so_fpd bs=IN1	if so_fpd <> 0 and mode = "I" then\		so_npd = so_fpd: display so_npd: goto IN4IN3     input so_npd bs=IN2IN4     input so_lpd - so_freq bs=IN3	if so_freq = "D" then\		input so_days bs=IN4	returnIN9     clear: display date	if mode = "A" then gosub DISPLAYEND     end