!file addr!title print!title print #dw;"Telephone Numbers";#sw!title print!heading printh *sur,*name,tab(36);"Page ";pageno!heading print!final print: print "End of list"        if htel = "" and otel = "" then end        keep 6        printh sur,name,        if htel then gosub HTEL        if otel then print "Office: ";otel        print        endHTEL    print "  Home: ";htel        if otel then printh [sur,name],        return